return [{
  json: {
    attached_media: $input.all().map(item => ({
      media_fbid: item.json.id // Giữ danh sách media_fbid từ các item trước
    })),
  }
}];

const list = [];
for(const item of $('Data').all()) {
  list.push({
    ... item.json,
    accessToken: $('FindPage').first().json["Access Token"],
    Link: 
      [item.json["Ảnh 1(Đại diện)"],
      item.json['Ảnh 2'],
      item.json['Ảnh 4'],
      item.json['Ảnh 5'],
      item.json['Ảnh 6'],
      item.json['Ảnh 7'],
      item.json['Ảnh 8'],
      item.json['Ảnh 9'],
      item.json['Ảnh 10']
    ].filter(x => !!x),
    content: $input.last().json.content,
    title: $input.last().json.title,
  })
}
return list;

