// === Helpers ===
function decodeRFC2047(str = '') {
  // decode =?charset?B|Q?encoded?=
  return str.replace(/=\?([^?]+)\?([bBqQ])\?([^?]+)\?=/g, (_, cs, enc, txt) => {
    try {
      if (/b/i.test(enc)) {
        return Buffer.from(txt, 'base64').toString('utf8');
      } else {
        // Q-encoding: _ => space, =HH => byte
        let s = txt.replace(/_/g, ' ');
        return s.replace(/=([0-9A-Fa-f]{2})/g, (_, h) =>
          String.fromCharCode(parseInt(h, 16))
        );
      }
    } catch {
      return txt;
    }
  });
}

function sanitizeName(name) {
  // loại ký tự cấm, cắt độ dài vừa phải
  name = name.replace(/[\\/:*?"<>|]/g, '-').trim();
  if (name.length > 180) name = name.slice(0, 180);
  // tránh tên trống
  if (!name) name = 'attachment';
  return name;
}

function extFromMime(mime = '') {
  if (!mime) return '';
  const map = {
    'application/pdf': 'pdf',
    'image/jpeg': 'jpg',
    'image/jpg': 'jpg',
    'image/png': 'png',
    'image/gif': 'gif',
    'image/webp': 'webp',
    'image/tiff': 'tif',
    'text/plain': 'txt',
    'text/csv': 'csv',
    'application/json': 'json',
    'application/zip': 'zip',
    'application/x-7z-compressed': '7z',
    'application/x-rar-compressed': 'rar',
    'application/vnd.ms-excel': 'xls',
    'application/vnd.ms-powerpoint': 'ppt',
    'application/msword': 'doc',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document': 'docx',
    'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': 'xlsx',
    'application/vnd.openxmlformats-officedocument.presentationml.presentation': 'pptx',
  };
  if (map[mime]) return map[mime];

  // fallback chung
  const parts = mime.split('/');
  if (parts.length === 2) {
    if (parts[0] === 'application' && parts[1].startsWith('vnd.')) {
      // đoán Office OOXML theo từ khoá
      if (parts[1].includes('word')) return 'docx';
      if (parts[1].includes('excel')) return 'xlsx';
      if (parts[1].includes('powerpoint')) return 'pptx';
    }
    return parts[1].replace('x-','').replace('octet-stream','');
  }
  return '';
}

function ensureExt(name, mime, fallbackExt) {
  // nếu name đã có .ext hợp lý thì giữ; nếu chưa, thêm từ mime hoặc fallback
  const hasDot = /\.[a-zA-Z0-9]{1,5}$/.test(name);
  if (hasDot) return name;
  const ext = extFromMime(mime) || (fallbackExt || '').replace(/^\./,'');
  return ext ? `${name}.${ext}` : name;
}

// === Main ===
const out = [];

for (const item of $input.all()) {
  const from = item.json?.from || '';
  const subject = item.json?.subject || '';
  const date = item.json?.date || '';

  const binaries = item.binary || {};
  for (const [key, bin] of Object.entries(binaries)) {
    // Bắt các prefix phổ biến từ IMAP node
    if (!(key.startsWith('attachment_') || key.startsWith('att_'))) continue;

    const rawName = bin.fileName || key;
    const decodedBase = sanitizeName(
      decodeRFC2047(rawName)
        .replace(/\s*=\.$/, '') // dọn tàn dư "=. "
        .replace(/\.+$/, '')     // bỏ dấu . cuối
    );

    // ưu tiên ext từ fileName nếu đã có; nếu không, lấy từ mime hoặc fileExtension
    const candidateExt = bin.fileExtension || '';
    let finalName = ensureExt(decodedBase, bin.mimeType || '', candidateExt);

    // Tránh trùng tên (tuỳ chọn): thêm timestamp nếu cần
    // const stamp = new Date().toISOString().replace(/[:.]/g, '-');
    // finalName = `${stamp}-${finalName}`;

    out.push({
      json: {
        fileName: finalName,
        mimeType: bin.mimeType || 'application/octet-stream',
        size: bin.fileSize || null,
        from, subject, date,
      },
      binary: { data: bin } // chuẩn hoá để node Upload đọc "data"
    });
  }
}

// Không có đính kèm -> trả item thông báo nhẹ (không lỗi)
if (out.length === 0) {
  return [{ json: { info: 'Email không có file đính kèm.' } }];
}

return out;

const norm = s => String(s||'').normalize('NFC').trim();
const normKey = s => norm(s).toLowerCase();

const inputData = $input.all();

const seen = new Set();
const unique = [];

for (const item of inputData) {
  const value = item.json.output || item.json.content;
  if (!value) continue;

  const k = normKey(value);
  if (!seen.has(k)) {
    seen.add(k);
    unique.push(norm(value));
  }
}

return [
  {
    json: {
      output_all: unique.join('\n\n')
    }
  }
];
